import "./App.css";
import MainSection from "./components/section1/mainSection";
import ClickSpark from "./components/spark/spark";
import Plasma from "./components/Plasma/plasma";

function App() {
  return (
    <ClickSpark
  sparkColor="#fff"
  sparkSize={30}
  sparkRadius={60}
  sparkCount={5}
  duration={700}
>
  {/* Plasma background */}
  <div
    style={{
      position: "fixed",
      top: 0,
      left: 0,
      width: "100vw",
      height: "100vh",
      zIndex: -10,
    }}
  >
    <Plasma
      color="#ff6b35"
      speed={0.6}
      direction="forward"
      scale={1.1}
      opacity={0.8}
      mouseInteractive={true}
    />
  </div>

  {/* Foreground content */}
  <div className="relative w-full flex flex-col items-center gap-20">
    <MainSection />
    <MainSection />
  </div>
</ClickSpark>
  );
}
export default App;
